import React, { useEffect, useState } from 'react';
import jwt from 'jsonwebtoken'
import { useNavigate } from 'react-router-dom'

const Dashboard = () => {
    const [balance, setbalance]     = React.useState(true);
    const [tempBalance, setTempBalance] = React.useState('');  
  
    return (
      <Card
        bgcolor="warning"
        header="Deposit"
        tempBalance={tempBalance}
        body={balance ? 
          <DepositForm setbalance={setbalance} setTempBalance={setTempBalance}/> :
          <DepositMsg setbalance={setbalance}/>}
      />
    )
  }
  
  function DepositMsg(props){
    return (<>
      <h5>Success</h5>
      <button type="submit" 
        className="btn btn-light" 
        onClick={() => props.setbalance(true)}>
          Deposit again
      </button>
    </>);
  } 
  
  function DepositForm(props){
    const [email, setEmail]   = React.useState('');
    const [amount, setAmount] = React.useState('');
    const ctx = React.useContext(UserContext);  
  
    function handle(){
      console.log(email,amount);
      const user = ctx.users.find((user) => user.email == email);
      if (!user) {
        props.setTempBalance('fail!');
        return;      
      }
  
      user.balance = user.balance + Number(amount);
      console.log(user);
      props.setTempBalance('');      
      props.setbalance(false);
    }
  
    return(<>
  
      Email<br/>
      <input type="input" 
        className="form-control" 
        placeholder="Enter email" 
        value={email} onChange={e => setEmail(e.currentTarget.value)}/><br/>
        
      Amount<br/>
      <input type="number" 
        className="form-control" 
        placeholder="Enter amount" 
        value={amount} onChange={e => setAmount(e.currentTarget.value)}/><br/>
  
      <button type="submit" 
        className="btn btn-light" 
        onClick={handle}>Deposit</button>
  
    </>);
  

    
    // return (
    //     <div class="register-or-login">
    // <div class="test1 overlay" data-aos="fade" id="home-section">
    // <div class="container">
    // <div class="row align-items-center justify-content-center">
    // <div class="col-md-12 mt-lg-5 text-center">
    //         <h1>Your balance: {balance || 'No balance found'}</h1>
    //         <form onSubmit={updateBalance}>
    //             <input
    //                 type='text'
    //                 placeholder='balance'
    //                 value={tempBalance}
    //                 onChange={(e) => setTempBalance(e.target.value)}
    //             />
    //             <input type="submit" value="Update balance" />
    //         </form>
    //     </div>
    //     </div>
    //     </div>
    //     </div>
    //     </div>
    // )
        
}

export default Dashboard
