import React, { useState } from "react";


function App() {
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')

  async function loginUser(event) {
    event.preventDefault()

    const response = await fetch('http://localhost:1337/api/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        email,
        password,
      })
    })

    const data = await response.json()

    if(data.user) {
      localStorage.setItem('token', data.user)
      alert('Login succesful')
      window.location.href = '/dashboard'
    } else {
      alert('Please check your username and password')
    }
  }




  return (
    <div class="register-or-login">
    <div class="test1 overlay" data-aos="fade" id="home-section">
    <div class="container">
    <div class="row align-items-center justify-content-center">
    <div class="col-md-12 mt-lg-5 text-center">
      <h1 class="form__title">Login</h1>
      <form class="form" id="login" onSubmit={loginUser}>
        <input class="form__input"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          type="text"
          autofocus placeholder="Email"
        /><br />
        <input class="form__input"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          type="text"
          autofocus placeholder="Password"
        /><br />
        <button class="form__button" type="submit">Login</button>
        <p class="form__text">
          <a href="forgot_pass.html" class="forgot_pass.html">Forgot your password?</a>
        </p>
        <p class="form__text2">
          <a class="form__link" href="register" id="create.html">Don't have an account? Create account</a>
        </p>
      </form>
    </div>
    </div>
    </div>
    </div>
    </div>
  );
}

export default App;
