import { useState } from "react";
import { useNavigate } from 'react-router-dom'


function App() {
  const history = useNavigate()
  const [name, setName] = useState('')
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')

  async function registerUser(event) {
    event.preventDefault()

    const response = await fetch('http://localhost:1337/api/register', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        name,
        email,
        password,
      })
    })

    const data = await response.json()

    if(data.status === 'ok') {
      history('/login')
    }
  }




  return (
    <div class="register">
    <div class="test1 overlay" data-aos="fade" id="home-section">
    <div class="container">
    <div class="row align-items-center justify-content-center">
    <div class="col-md-12 mt-lg-5 text-center">
      <h1 class="form__title">Register</h1>
      <form class="form" id="login" onSubmit={registerUser}>
        <input class="form__input"
          value={name}
          onChange={(e) => setName(e.target.value)}
          type="text"
          autofocus placeholder="Name"
        /><br />
        <input class="form__input"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          type="text"
          autofocus placeholder="Email"
        /><br />
        <input class="form__input"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          type="text"
          autofocus placeholder="Password"
        /><br />
        <button class="form__button" type="submit">Continue</button>
        <p class="form__text">
                <a class="form__link" href="login.html" id="linkLogin">Already have an account? Sign in</a>
        </p>
      </form>
    </div>
    </div>
    </div>
    </div>
    </div>
  );

}

export default App;
