import React, { useEffect, useState } from 'react';
import jwt from 'jsonwebtoken'
import { useNavigate } from 'react-router-dom'

const Dashboard = () => {
    const history = useNavigate()
    const [balance, displayBalance] = useState('')
    const [newDeposit, setNewDeposit] = useState('')
    const [newWithdraw, setNewWithdraw] = useState('')
    async function populateBalance() {
        const req = await fetch('http://localhost:1337/api/balance', {
            headers: {
                'x-access-token': localStorage.getItem('token'),
            },
        })

        const data = await req.json()
        if(data.status === 'ok') {
            displayBalance(data.balance)
        } else {
            alert(data.error)
        }
    }

    useEffect(() => {
        const token = localStorage.getItem('token')
        if (token) {
            const user = jwt.decode(token)
            if(!user) {
                localStorage.removeItem('token')
                history.replace('/login')
            } else {
                populateBalance()
            }
        }
    })

    async function Deposit() {

        const req = await fetch('http://localhost:1337/api/balance', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'x-access-token': localStorage.getItem('token'),
            },
            body: JSON.stringify({
                balance: +balance + +newDeposit,
            }),
        })

        const data = await req.json()
        if(data.status === 'ok') {
            setNewDeposit('')
            displayBalance(newDeposit)
        } else {
            alert(data.error)
        }

    }

    async function Withdraw() {

        const req = await fetch('http://localhost:1337/api/balance', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'x-access-token': localStorage.getItem('token'),
            },
            body: JSON.stringify({
                balance: +balance - +newWithdraw,
            }),
        })

        const data = await req.json()
        if(data.status === 'ok') {
            setNewWithdraw('')
            displayBalance(newWithdraw)
        } else {
            alert(data.error)
        }

    }
    
    return (
        <div class="dashboard_container">

        <div class="balance"><h1>Your balance: ${balance || 'No balance found'}</h1></div>    
        <div class="deposit">
            <div class="test1 overlay" id="home-section">
                <div class="container">
                    <div class="row align-items-center justify-content-center">
                        <div class="col-md-12 mt-lg-5 text-center">
                                <form onSubmit={Deposit}>
                                    <input
                                        type='text'
                                        placeholder='Amount'
                                        value={newDeposit}
                                        onChange={(e) => setNewDeposit(e.target.value)}
                                    />
                                <input type="submit" value="Deposit" />
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>

        <div class="withdraw">
            <div class="test1 overlay"  id="home-section">
                <div class="container">
                    <div class="row align-items-center justify-content-center">
                        <div class="col-md-12 mt-lg-5 text-center">
                                <form onSubmit={Withdraw}>
                                    <input
                                        id="withdraw"
                                        type='text'
                                        placeholder='Amount'
                                        value={newWithdraw}
                                        onChange={(e) => setNewWithdraw(e.target.value)}
                                    />
                                <input type="submit" value="Withdraw" />
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
        </div>
        
    )
        
}

export default Dashboard
