import React, { useEffect, useState } from 'react';
import jwt from 'jsonwebtoken'
import { useNavigate } from 'react-router-dom'

const Dashboard = () => {
    const history = useNavigate()
    const [balance, setBalance] = useState('')
    const [tempBalance, setTempBalance] = useState('')

    async function populateBalance() {
        const req = await fetch('http://localhost:1337/api/balance', {
            headers: {
                'x-access-token': localStorage.getItem('token'),
            },
        })

        const data = await req.json()
        if(data.status === 'ok') {
            setBalance(data.balance)
        } else {
            alert(data.error)
        }
    }

    useEffect(() => {
        const token = localStorage.getItem('token')
        if (token) {
            const user = jwt.decode(token)
            if(!user) {
                localStorage.removeItem('token')
                history.replace('/login')
            } else {
                populateBalance()
            }
        }
    })

    async function updateBalance() {

        const client = await getClient();
        try {
          await client.query('begin');
          const { transaction_date, deposit_amount } = req.body;
          const account_id = req.params.id;
          const result = await client.query(
            'select total_balance from account where account_id=$1',
            [account_id]
          );
          const total_balance = +result.rows[0].total_balance;
          const total = total_balance + deposit_amount;
          await client.query(
            'insert into transactions(transaction_date, deposit_amount, account_id, balance) values($1,$2,$3,$4) returning *',
            [transaction_date, deposit_amount, account_id, total]
          );
          await client.query(
            'update account set total_balance = total_balance + $1 where account_id=$2',
            [deposit_amount, account_id]
          );
          await client.query('commit');
          res.send();
        } catch (error) {
          await client.query('rollback');
          res.status(400).send({
            add_error: 'Error while depositing amount..Try again later.'
          });
        } finally {
          client.release();
        }
      }
    
    return (
        <div class="register-or-login">
    <div class="test1 overlay" data-aos="fade" id="home-section">
    <div class="container">
    <div class="row align-items-center justify-content-center">
    <div class="col-md-12 mt-lg-5 text-center">
            <h1>Your balance: {balance || 'No balance found'}</h1>
            <form onSubmit={updateBalance}>
                <input
                    type='text'
                    placeholder='balance'
                    value={tempBalance}
                    onChange={(e) => setTempBalance(e.target.value)}
                />
                <input type="submit" value="Update balance" />
            </form>
        </div>
        </div>
        </div>
        </div>
        </div>
    )
        
}

export default Dashboard
