import React, { useEffect, useState } from 'react';
import jwt from 'jsonwebtoken'
import { useNavigate } from 'react-router-dom'

const Dashboard = () => {
    const history = useNavigate()
    const [balance, setBalance] = useState('')
    const [tempBalance, setTempBalance] = useState('')

    async function populateBalance() {
        const req = await fetch('http://localhost:1337/api/balance', {
            headers: {
                'x-access-token': localStorage.getItem('token'),
            },
        })

        const data = await req.json()
        if(data.status === 'ok') {
            setBalance(data.balance)
        } else {
            alert(data.error)
        }

        const user = localStorage.users.find((user) => user.email == email);
        if (!user) {
          props.setStatus('fail!');
          return;      
        }
    
        user.balance = user.balance + Number(amount);
        console.log(user);
        props.setStatus('');      
        props.setShow(false);
    }

    useEffect(() => {
        const token = localStorage.getItem('token')
        if (token) {
            const user = jwt.decode(token)
            if(!user) {
                localStorage.removeItem('token')
                history.replace('/login')
            } else {
                populateBalance()
            }
        }
    })

    async function updateBalance() {

        const req = await fetch('http://localhost:1337/api/balance', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'x-access-token': localStorage.getItem('token'),
            },
            body: JSON.stringify({
                balance: tempBalance,
            }),
        })

        const data = await req.json()
        if(data.status === 'ok') {
            setTempBalance('')
            setBalance(data.balance)
        } else {
            alert(data.error)
        }

    }
    
    return (
        <div class="register-or-login">
    <div class="test1 overlay" data-aos="fade" id="home-section">
    <div class="container">
    <div class="row align-items-center justify-content-center">
    <div class="col-md-12 mt-lg-5 text-center">
            <h1>Your balance: {balance || 'No balance found'}</h1>
            <form onSubmit={updateBalance}>
                <input
                    type='text'
                    placeholder='balance'
                    value={tempBalance}
                    onChange={(e) => setTempBalance(e.target.value)}
                />
                <input type="submit" value="Update balance" />
            </form>
        </div>
        </div>
        </div>
        </div>
        </div>
    )
        
}

export default Dashboard
